                        if (filesystem.readonly && zfsmanager.configuration.zfs.filesystem.readonlylockdown || pool.readonly) {
                            $("#div-" + modal.name + "-aclinherit-dropdown-" + modal.id + " button").prop("disabled", true);
                        }

                        break;
